    private void ConfirmarCadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConfirmarCadastroActionPerformed
        // TODO add your handling code here:
        CadastroFuncionario cadastro = new CadastroFuncionario();
        cadastro.setUsuario(UsuarioCadastro.getText().toString());
        cadastro.setSenha(SenhaCadastro.getText().toString());
        cadastro.setNome(NomeCadastro.getText().toString());
        cadastro.setNascimento(NascimentoCadastro.getText().toString());
        cadastro.setCpf(CpfCadastro.getText().toString());
        cadastro.setRua(RuaCadastro.getText().toString());
        cadastro.setNumero(Integer.parseInt(NumCadastro.getText().toString()));
        cadastro.setBairro(BairroCadastro.getText().toString());
        cadastro.setCidade(CidadeCadastro.getText().toString());
        cadastro.setEstado(EstadoCadastro.getText().toString());
        
        funcionarioDAO.inserir(cadastro);
        
        JOptionPane.showMessageDialog(null, "O Usuario " + UsuarioCadastro.getText().trim() + " foi cadastrado !!");
        
    }//GEN-LAST:event_ConfirmarCadastroActionPerformed

